let payload = msg.payload; // Incoming MQTT payload
let topic = msg.topic;     // MQTT topic (e.g., water_quality/L'Aquila Distribution Zone/Main Tank)

// Parse topic hierarchy
let parts = topic.split('/');
let zone = parts[1];        // Extract the zone
let device = parts[2];      // Extract the device (e.g., "Main Tank" or "Secondary Tank")
let isPipeline = parts.length === 4; // Check if it's a pipeline

if (isPipeline) {
    return null; // Ignore pipeline data
}

// Convert timestamp to nanoseconds (InfluxDB default)
let timestamp = payload.timestamp ? payload.timestamp * 1e9 : Date.now() * 1e6; // Use current time if missing

// Construct the payload
msg.measurement=isPipeline ? "pipelines" : "tanks"
msg.payload = [{
        ORP: payload.ORP,
        pH: payload.PH,
        Salinity: payload.Salinity,
        Water_Depth: payload.Water_Depth,
        Turbidity: payload.Turbidity,
        Temperature: payload.Temperature,
        latitude: payload.latitude,
        longitude: payload.longitude,
        time: timestamp / 1000000 //Converting to miliseconds
    },{
        measurement: isPipeline ? "pipelines" : "tanks",
        zone: zone,
        tank: device,
        device: device,
        type: "tank",
    }];

return msg;

let payload = msg.payload; // Incoming MQTT payload
let topic = msg.topic;     // MQTT topic (e.g., water_quality/L'Aquila Distribution Zone/Main Tank)

// Parse topic hierarchy
let parts = topic.split('/');
let zone = parts[1];        // Extract the zone
let tank = parts[2]; 
let device = parts[3];      // Extract the device (e.g., "Main Tank" or "Secondary Tank")
let isPipeline = parts.length === 4; // Check if it's a pipeline

if (!isPipeline) {
    return null; // Ignore pipeline data
}

// Convert timestamp to nanoseconds (InfluxDB default)
let timestamp = payload.timestamp ? payload.timestamp * 1e9 : Date.now() * 1e6; // Use current time if missing

// Construct the payload
msg.measurement=isPipeline ? "pipelines" : "tanks"
msg.payload = [{
            pipeline: parts[3],
            pH: payload.PH,
            Salinity: payload.Salinity,
            Flow: payload.Flow,
            latitude: payload.latitude,
            longitude: payload.longitude,
            time: timestamp / 1000000 //Converting to miliseconds
            },{
            measurement: isPipeline ? "pipelines" : "tanks",
            zone: zone,
            tank: tank,
            device: device,
            type: "pipelines",
        }];

return msg;

// Store the parsed JSON in global context
global.set("thresholds", msg.payload);

// Pass the message through for debugging or further use
return msg;

// Get thresholds from global context
const thresholds = global.get("thresholds");
const alerts = [];

// Check if thresholds exist
if (!thresholds) {
    node.error("Thresholds not loaded in global context!");
    return msg;
}

const fields = Object.keys(thresholds);

// Loop through the sensor data from InfluxDB
msg.payload.forEach((data) => {

    fields.forEach(field => {

        if (data[field]) { //Check if field exists in the data

            const { alert_low, alert_high } = thresholds[field];
            let value = data[field];
            let device = data["device"];

            // Compare value with thresholds
            if (value < alert_low) {
                alerts.push(`ðŸš¨ ${device} (${field}): Too Low (${value})`);
            } else if (value > alert_high) {
                alerts.push(`ðŸš¨ ${device} (${field}): Too High (${value})`);
            }
        }
    });
});

// Send warnings if any
if (alerts.length > 0) {
    msg.payload = {
        chatId: -4797952575,
        type: "message",
        content: alerts.join("\n")
    }
    return msg; // Pass the message to the next node
}

// If no warnings, stop the flow
return null;

const data = msg.payload; // Input JSON
const points = [];

for (const metric in data) {
    const thresholds = data[metric];
    points.push([
        { 
            alert_low: thresholds.alert_low, 
            alert_high: thresholds.alert_high 
        },
        { metric }        // Tag (e.g., Flow, pH)
    ]);
}

msg.payload = points; // Output data points for InfluxDB
return msg;
